mapper.insert:
insert into gitstatistics (id, application, name, 
  added, removed, updated, 
  time, status, addno, 
  addtime, updateno, updatetime
  )
values (#{id,jdbcType=INTEGER}, #{application,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
  #{added,jdbcType=INTEGER}, #{removed,jdbcType=INTEGER}, #{updated,jdbcType=INTEGER}, 
  #{time,jdbcType=TIMESTAMP}, #{status,jdbcType=CHAR}, #{addno,jdbcType=VARCHAR}, 
  #{addtime,jdbcType=TIMESTAMP}, #{updateno,jdbcType=VARCHAR}, #{updatetime,jdbcType=TIMESTAMP}
  )
